import React, { useState, useEffect, useRef } from 'react';

const SOCKET_URL = 'wss://xubzgv3dv1.execute-api.us-east-1.amazonaws.com/production/'; // Replace with your real URL

export default function ChatApp() {
  const [ws, setWs] = useState(null);
  const [messages, setMessages] = useState([]);
  const [name, setName] = useState('');
  const [connected, setConnected] = useState(false);
  const [input, setInput] = useState('');
  const [privateTo, setPrivateTo] = useState('');
  const [members, setMembers] = useState([]);

  const messagesEndRef = useRef();

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  useEffect(() => {
    if (ws) {
      ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.members) {
          setMembers(data.members);
        } else {
          setMessages(prev => [...prev, data]);
        }
      };
    }
  }, [ws]);

  const connect = () => {
    const socket = new WebSocket(SOCKET_URL);
    socket.onopen = () => {
      setWs(socket);
      setConnected(true);
      socket.send(JSON.stringify({ action: 'setName', name }));
    };
    socket.onclose = () => {
      setConnected(false);
      setWs(null);
    };
  };

  const sendPublic = () => {
    if (input) {
      ws.send(JSON.stringify({ action: 'sendPublic', message: input }));
      setInput('');
    }
  };

  const sendPrivate = () => {
    if (input && privateTo) {
      ws.send(JSON.stringify({ action: 'sendPrivate', message: input, to: privateTo }));
      setInput('');
    }
  };

  return (
    <div className="flex h-screen font-sans">
      <div className="w-1/4 bg-purple-900 text-white p-4 space-y-2 overflow-y-auto">
        {members.map((member, i) => (
          <div key={i} className="p-2 rounded hover:bg-purple-800 cursor-pointer" onClick={() => setPrivateTo(member)}>
            {member}
          </div>
        ))}
      </div>

      <div className="flex flex-col w-3/4 p-4">
        {!connected ? (
          <div className="flex gap-2">
            <input
              className="border px-4 py-2 rounded"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <button
              className="bg-purple-700 text-white px-4 py-2 rounded"
              onClick={connect}
              disabled={!name}
            >
              Connect
            </button>
          </div>
        ) : (
          <>
            <div className="flex-1 border rounded p-4 overflow-y-auto mb-4 bg-gray-50">
              {messages.map((msg, i) => (
                <div key={i} className="mb-2">
                  {Object.entries(msg).map(([key, val]) => (
                    <div key={key}><strong>{key}</strong>: {val}</div>
                  ))}
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>

            <div className="flex gap-2">
              <input
                className="border px-4 py-2 flex-1 rounded"
                placeholder="Write a message..."
                value={input}
                onChange={(e) => setInput(e.target.value)}
              />
              <button className="bg-purple-600 text-white px-4 py-2 rounded" onClick={sendPublic}>
                Send Public
              </button>
              <button className="bg-blue-600 text-white px-4 py-2 rounded" onClick={sendPrivate}>
                Send Private to: {privateTo || '...'}
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
